import { Button, Form, FormGroup, Input, Container } from 'reactstrap'
import { BrowserRouter as Router, Switch, Route, NavLink } from "react-router-dom";


export default class Login extends React.Component {

    constructor(props) {
        super(props)

        // this.overlayDiv = React.createRef()

        // this.handleUser1Change = this.handleUser1Change.bind(this)
        // this.handleUser2Change = this.handleUser2Change.bind(this)
    }

    render() {
        return (
        <div id="wrapperDiv">
            <Container id="mainDiv">
                <Router>
                    <h1 className="mt-5 mb-10" style={{color: "#ffffff"}}>Unify</h1>
                    <NavLink exact className="btn btn-lg btn-block" to="/home" style={{backgroundColor: "#1DB954", border: "none", outline: "none"}}>Login</NavLink>

                    <Switch>
                        <Route path="/home">
                            <Home />
                        </Route>
                        <Route path="/">
                            <Login />
                        </Route>
                    </Switch>
                </Router> 
            </Container>
        </div>
        )
    }
}
